mapper.insert:
	
		INSERT INTO PICTURE_BOARD_REPLY(		
			BOARD_NO,
			CONTENT,
			WRITER
		) VALUES (
			#{boardNo},
			#{content},
			#{writer}
		)

mapper.update:

	UPDATE PICTURE_BOARD_REPLY
	
	SET		
		CONTENT = #{content}
	
	WHERE SEQ_NO = #{seqNo};

UPDATE PICTURE_BOARD_REPLY
SET USE_YN = 'N'
WHERE SEQ_NO = #{seqNo};

UPDATE	PICTURE_BOARD_FAVORITE
SET		USE_YN = 'N'
WHERE	BOARD_NO = #{boardNo} AND MEMBER_ID = #{id};

mapper.select:
SELECT	USE_YN
FROM	PICTURE_BOARD_FAVORITE
WHERE 	MEMBER_ID = #{id} AND BOARD_NO = #{seqNo} ;
